"use strict";
var jCars = (function() {
    var hd = "",
        //grid = "",
        pmsg = "",
        v = false,
        obj = "",
     s = false;
    var grid = '#gcars';

    function init(x) {
        hd = new Gtaxi();
        hd.setkey(x);
    }

    function rd() {
        if (hd instanceof Gtaxi) {
            return hd;
        } else {
            return undefined;
        }
    }

    function a(d, e, g) {
        $(grid).datagrid("load", {
            search : e,
            keyword : g,
            'taxixkey' : rd().getkey()
        });
        setTimeout(function () {
            $('tr>td:contains("' + g + '")').css({
                background : "yellow",
                color : "black",
                "font-weight" : "bold"
            })
        }, 1000)
    }
    function dosearch(g, o, l, d) {
        var n = "",
        b = 1500,
        k = "",
        j = "",
        h = "",
        i = "",
        f = null,
        j = document.getElementById(g),
        h = document.getElementById(o),
        i = d || "1";
        j.onchange = function () {
            h.value = "";
            k = j.options[j.selectedIndex].value;
            if (k == "") {
                return
            }
            h.onkeyup = function () {
                this.value = this.value.toUpperCase().replace(/[^A-Z0-9\s\.\_\-]/g, "");
                f = this.value.toUpperCase().replace(/[^A-Z0-9\s\.\_\-]/g, "");
                if (parseInt(f.length) > parseInt(i)) {
                    clearTimeout(n);
                    n = setTimeout(function () {
                            a(l, k, f)
                        }, b)
                } else {
                    return
                }
            };
            h.keydown = function () {
                clearTimeout(n)
            }
        }
    }

    function loadorphanimei(n1, n2) {
        var opt = ['<option value="">------------</option>'];
        rd().sendto('taxidevices/orphanimei', null, 'get').done(function(rsp) {
            pmsg = $.parseJSON(rsp);
            if (parseInt(pmsg.length) > 0) {
                $.each(pmsg, function(i, c) {
                    opt.push('<option value="' + c.nik + '">' + c.imei + '</option>');
                });
            } else {
                opt.push('<option value="">Daftarkan perangkat terlebih dahulu</option>');
            }
            $('form[id="' + n1 + '"] select[name="' + n2 + '"]').html(opt);
        }).fail(function() {
            opt.push('<option value="">Daftarkan perangkat terlebih dahulu</option>');
            $('form[id="' + n1 + '"] select[name="' + n2 + '"]').html(opt);
        });
    }
    return {
        init: function(a) {
            init(a);
        },
        searchgrid : function () {
            dosearch("terminalsearch", "terminalkeyword", "gcars","0");
        },
        grid: function() {
            $(grid).datagrid({
                url: rd().site_url("cars/selects"),
                nowrap: true,
                singleSelect: true,
                rownumbers: true,
                fit: true,
                striped: true,
                autoRowHeight: false,
                pagination: true,
                fitColumns: false,
                method: "post",
                halign: "center",
                queryParams: {
                    'taxixkey': rd().getkey()
                },
                columns: [
                    [{
                        halign: "center",
                        align: "center",
                        field: "taxinumber",
                        title: "No.Taksi",
                        width: "100"
                    }, {
                        halign: "center",
                        align: "center",
                        field: "policenumber",
                        title: "No.Polisi",
                        width: "100"
                    },{
                        halign: "center",
                        align: "center",
                        field: "car_status",
                        title: "Status",
                        width: "100"
                    },{
                        halign: "center",
                        align: "center",
                        field: "fullname",
                        title: "Nama Driver",
                        width: "100"
                    }]
                ]
            });
        },
        dlginserttaxi: function() {
            rd().opndlg("#dlgitaxi", "Daftar Taksi");
            document.forms['fctaxi'].reset();
            //loadorphanimei('fctaxi', 'imei');
        },
        dlgedittaxi: function() {
            s = $(grid).datagrid('getSelected');
            if(s){
                document.forms['fetaxi'].reset();
                rd().opndlg("#dlgetaxi","Edit Taksi " + s.taxi_number);
                $("#fetaxi").form("load", s);
            }else{
                $.messager.alert('info','Pilih data terlebih dahulu');
            }
        },
        inserttaxi: function(u) {
            v = $('#fctaxi').form('validate');
            if (v) {
                obj = $('form[id="fctaxi"]').serialize() + '&taxixkey=' + rd().getkey();
                rd().sendto(u, obj).done(function(rsp) {
                    pmsg = $.parseJSON(rsp);
                    if (pmsg.result == 'success') {
                        rd().clsdlg('#dlgitaxi');
                        rd().messageok('Daftar Taksi', pmsg.message);
                        rd().rload(grid);
                    } else {
                        rd().messageok('Daftar Taksi', pmsg.message);
                    }
                }).fail(function() {
                    rd().messagebad('Daftar Perangkat');
                });
            }
        },
        update: function(u) {
            v = $('#fetaxi').form('validate');
            if(v){ 
                obj = $('form[id="fetaxi"]').serialize() + '&taxixkey=' + rd().getkey();
                rd().sendto(u, obj).done(function(rsp) {
                    pmsg = $.parseJSON(rsp);
                    if (pmsg.result == 'success') {
                        rd().messageok('Edit Sopir', pmsg.message);
                        rd().clsdlg("#dlgetaxi");
                        rd().rload(grid);
                    } else {
                        rd().messagebad('Edit Sopir', pmsg.message);
                    }
                }).fail(function() {
                    rd().messagebad('Edit Sopir', 'Errors occured.Proccess terminated');
                });
            }
        },
        dlgaddtxdevice: function() {
            s = $(grid).datagrid('getSelected');
            if (s) {
                if (s.imei == '' || s.imei == null) {
                    rd().opndlg('#dlgaddtxdvc', 'Menugaskan perangkat');
                    document.forms['faddtxdvc'].reset();
                    $('#faddtxdvc').form('load', s);
                    loadorphanimei('faddtxdvc', 'imei');
                } else {
                    $.messager.alert("Information", "Hanya taksi tidak mempunyai imei yang diijinkan");
                }
            } else {
                $.messager.alert("Information", "Silakan pilih data terlebih dahulu");
            }
        },
        addtxdevice: function(u) {
            v = $('#faddtxdvc').form('validate');
            if (v) {
                if($('form[id="faddtxdvc"] select[name="imei"]').val() != ''){
                    obj = $('form[id="faddtxdvc"]').serialize() + '&taxixkey=' + rd().getkey();
                    rd().sendto(u, obj).done(function(rsp) {
                        pmsg = $.parseJSON(rsp);
                        if (pmsg.result == 'success') {
                            rd().clsdlg('#dlgaddtxdvc');
                            rd().messageok('Menugaskan Perangkat', pmsg.message);
                            rd().rload(grid);
                        } else {
                            rd().messageok('Menugaskan Perangkat', pmsg.message);
                        }
                    }).fail(function() {
                        rd().messagebad('Menugaskan Perangkat');
                    });
                }else{
                    rd().messageok('Menugaskan Perangkat', 'Silakan daftarkan imei perangkat!');
                    return;
                }
            }
        },
        deltxdevice: function(u) {
            s = $(grid).datagrid('getSelected');
            if (s) {
                console.info(s.imei);
                if (s.imei == '' || s.imei == null) {
                    $.messager.alert("Information", "Hanya taksi tidak mempunyai imei yang diijinkan");
                    return;
                }
                $.messager.confirm("Hapus perangkat - Konfirmasi", "Yakin akan menghapus perangkat : " + s.license_plate + " ?", function(c) {
                    if (c) {
                        obj = new Object({
                            'id_car': s.id_car,
                            'taxixkey': rd().getkey()
                        });
                        rd().sendto(u, obj).done(function(d) {
                            pmsg = $.parseJSON(d);
                            if (pmsg.result == "success") {
                                rd().messageok("Hapus perangkat taksi", pmsg.message);
                                rd().rload(grid);
                            } else {
                                rd().messagebad('Hapus perangkat taksi', pmsg.message);
                            }
                        }).fail(function() {
                            rd().messagebad('Hapus perangkat');
                        });
                    }
                });
            }
        },
        deltaxi: function(u) {
            s = $(grid).datagrid('getSelected');
            if (s) {
                $.messager.confirm("Hapus perangkat - Konfirmasi", "Yakin akan menghapus perangkat : " + s.license_plate + " ?", function(c) {
                    if (c) {
                        obj = new Object({
                            'id_car': s.id_car,
                            'taxixkey': rd().getkey()
                        });
                        rd().sendto(u, obj).done(function(d) {
                            pmsg = $.parseJSON(d);
                            if (pmsg.result == "success") {
                                rd().messageok("Perangkat taksi", pmsg.message);
                                rd().rload(grid);
                            } else {
                                rd().messagebad('Perangkat taksi', pmsg.message);
                            }
                        }).fail(function() {
                            rd().messagebad('Perangkat taksi');
                        });
                    }
                });
            }
        },
        dlginsertdevice: function() {
            rd().opndlg("#dlgiimei", "Daftar Perangkat");
            document.forms['fcimei'].reset();
        },
        insertdevice: function(u) {
            v = $('#fcimei').form('validate');
            if (v) {
                obj = $('form[id="fcimei"]').serialize() + '&taxixkey=' + rd().getkey();
                rd().sendto(u, obj).done(function(rsp) {
                    pmsg = $.parseJSON(rsp);
                    if (pmsg.result == 'success') {
                        rd().clsdlg('#dlgiimei');
                        rd().clsdlg('#dlgitaxi');
                        rd().clsdlg('#dlgaddtxdvc');
                        rd().messageok('Daftar Perangkat', pmsg.message);
                        rd().rload(grid);
                    } else {
                        rd().messagebad('Daftar Perangkat', pmsg.message);
                    }
                }).fail(function() {
                    rd().messagebad('Daftar Perangkat');
                });
            }
        }
    }
})();
$(function () {
    jCars.init('keys');
    jCars.grid();
    jCars.searchgrid()
});
