"use strict";
var jCustreq = (function() {
    var hd = "",
        grid = "";
    grid = '#gcustreq';

    function init(x) {
        hd = new Gtaxi();
        hd.setkey(x);
    }

    function rd() {
        if(hd instanceof Gtaxi){
            return hd;
        }else{
            return undefined;
        }
    }
    return {
        init: function(a) {
            init(a);
        },
        grid: function() {
            $(grid).datagrid({
                url: rd().site_url("custrequests/selects"),
                nowrap: true,
                singleSelect: true,
                rownumbers: true,
                fit: true,
                striped: true,
                autoRowHeight: false,
                pagination: true,
                fitColumns: false,
                method: "post",
                halign: "center",
                queryParams: {
                    'taxixkey':rd().getkey()
                },
                columns: [
                    [{
                        halign: "addrs_pickup",
                        field: "addrs_pickup",
                        title: "Alamat"
                    }, {
                        halign: "center",
                        field: "addrs_clue",
                        title: "Alamat Klue"
                    }, {
                        halign: "center",
                        field: "name",
                        title: "Nama"
                    }, {
                        halign: "center",
                        field: "latitude",
                        title: "LAT"
                    }, {
                        halign: "center",
                        field: "longitude",
                        title: "LONG"
                    }, {
                        halign: "center",
                        field: "name",
                        title: "Nama"
                    }, {
                        halign: "center",
                        field: "phone_number",
                        title: "Telepon",
                        width: 150
                    }, {
                        halign: "center",
                        field: "time_req",
                        title: "TIME REQUEST"
                    }, {
                        halign: "center",
                        field: "time_rsp",
                        title: "TIME RESPONSE"
                    }]
                ]
            });
        }
    }
})();
