var Gtaxi = (function() {
    function Gxtaxi() {
        //this.url = document.location.protocol + "//" + document.location.host + "/taksi-dev-manual/";
        this.url = document.location.protocol + "//" + document.location.host + "/webdriver/manual/";
        //this.url = document.location.protocol + "//localhost/webdriver/manual/home";
		this.qwe = "";
        this.width = "300";
        this.height = "100";
    }

    function b(c) {
        return c.replace(/[^a-zA-Z0-9\/=?&_]/gi, "");
    }

    function x(s, u, d, t) {
        var t = "";
        t = t || 'post';
        return $.ajax({
            url: s + b(u),
            type: t,
            data: d,
            cache: false
        });
    }
    Gxtaxi.prototype.base_url = function() {
        return this.url;
    };
    Gxtaxi.prototype.site_url = function(c) {
        return this.url + b(c);
    };
    Gxtaxi.prototype.setkey = function(d) {
        this.qwe = d;
    }
    Gxtaxi.prototype.getkey = function() {
        return this.qwe;
    }
    Gxtaxi.prototype.sendto = function(u, d, t) {
        return x(this.url, u, d, t);
    }
    Gxtaxi.prototype.messageok = function(b, a) {
        return $.messager.show({
            width: this.width,
            height: this.height,
            title: b,
            msg: a,
            showType: "show",
            style: {
                left: "",
                right: 0,
                top: document.body.scrollTop + document.documentElement.scrollTop,
                bottom: ""
            }
        });
    }
    Gxtaxi.prototype.messagebad = function(b, a) {
        return $.messager.show({
            width: this.width,
            height: this.height,
            title: b,
            msg: a || 'Error Occured. Process terminated',
            showType: "fade",
            style: {
                left: "",
                right: 0,
                bottom: ""
            }
        });
    }
    Gxtaxi.prototype.clsdlg = function(n) {
        return $(n).dialog('close');
    }
    Gxtaxi.prototype.opndlg = function(n, t) {
        return $(n).dialog('open').dialog("setTitle", t);
    }
    Gxtaxi.prototype.rload = function(g) {
        return $(g).datagrid('reload');
    }
    Gxtaxi.prototype.loadscript = function(src){
        var s = "",ss = "";
        s = document.getElementsByTagName('script')[0];
        ss = document.createElement('script');
        ss.type = "text/javascript";
        ss.async = true;
        ss.src = src;
        s.parentNode.insertBefore(ss, s);
    }
    return Gxtaxi;
})();
$.fn.datebox.defaults.formatter = function(b) {
    var e = "",
        a = "",
        c = "";
    e = b.getFullYear();
    a = b.getMonth() + 1;
    c = b.getDate();
    return e + "-" + a + "-" + c;
};