"use strict";
var jImei = (function() {
    var hd = "",
        v = false,
        s = "",
        pmsg = "",
        grid = "#gimei",
        kx = "",
        objs = "";

    function init(x) {
        hd = new Gtaxi();
        hd.setkey(x);
    }

    function rd() {
        if(hd instanceof Gtaxi){
            return hd;
        }else{
            return undefined;
        }
    }
    function a(d, e, g) {
        $(grid).datagrid("load", {
            search : e,
            keyword : g,
            'taxixkey' : rd().getkey()
        });
        setTimeout(function () {
            $('tr>td:contains("' + g + '")').css({
                background : "yellow",
                color : "black",
                "font-weight" : "bold"
            })
        }, 1000)
    }
    function dosearch(g, o, l, d) {
        var n = "",
        b = 1500,
        k = "",
        j = "",
        h = "",
        i = "",
        f = null,
        j = document.getElementById(g),
        h = document.getElementById(o),
        i = d || "1";
        j.onchange = function () {
            h.value = "";
            k = j.options[j.selectedIndex].value;
            if (k == "") {
                return
            }
            h.onkeyup = function () {
                this.value = this.value.toUpperCase().replace(/[^A-Z0-9\s\.\_\-]/g, "");
                f = this.value.toUpperCase().replace(/[^A-Z0-9\s\.\_\-]/g, "");
                if (parseInt(f.length) > parseInt(i)) {
                    clearTimeout(n);
                    n = setTimeout(function () {
                            a(l, k, f)
                        }, b)
                } else {
                    return
                }
            };
            h.keydown = function () {
                clearTimeout(n)
            }
        }
    }

    function gettaxi(n1,n2) {
        var temp = ['<option value="">-----------------</option>'];
        rd().sendto('imei/getnewtaxi', null).done(function(rsp){
            pmsg = $.parseJSON(rsp);
            console.info(pmsg.length);
            if(pmsg.length > 0){
                $.each(pmsg, function(i,c){
                    temp.push('<option value="'+c.id_car+'">'+c.taxi_number +' | ' +c.license_plate+'</option>');
                });
            }else{
                 temp.push('<option value="">Taxi baru tidak tersedia</option>');
            }
            $('form[id="' + n1 + '"] select[name="' + n2 + '"]').html(temp);
        }).fail(function(){
            temp.push('<option value="">Silahkan daftar taxi baru</option>');
            $('form[id="' + n1 + '"] select[name="' + n2 + '"]').html(temp);
        });
    }
    return {
        init: function(x) {
            init(x);
        },
        searchgrid : function () {
            dosearch("terminalsearch", "terminalkeyword", "gimei");
        },
        grid: function() {
            $(grid).datagrid({
                url: rd().site_url("imei/selects"),
                nowrap: true,
                singleSelect: true,
                rownumbers: true,
                fit: true,
                striped: true,
                autoRowHeight: false,
                pagination: true,
                fitColumns: false,
                method: "post",
                halign: "center",
                queryParams: {
                    'taxixkey':rd().getkey()
                },
                columns: [
                    [{
                        // halign: "center",
                        // field: "imei",
                        // hidden: true,
                        // title: "id"
                    // },{
                        // halign: "center",
                        // field: "name",
                        // hidden: true,
                        // title: "name"
                    // },{
                        halign: "center",
                        field: "imei",
                        title: "IMEI"
                    //},{
						//halign: "center",
                        //field: "taxi_number",
                        //title: "No.Taksi"
                    }, {
                        halign: "center",
                        field: "latitude",
                        title: "Latitude"
                    },{
                        halign: "center",
                        field: "longitude",
                        title: "Longitude"
                    },{
                        halign: "center",
                        field: "last_update",
                        title: "Last Update"
                    }]
                ]
            });
        },
        dlginsert: function() {
            rd().opndlg("#dlgiimei", "Daftar IMEI");
            document.forms['fcimei'].reset();
            gettaxi('fcimei','taxi_number');
        },
        insert: function(u) {
            v = $('#fcimei').form('validate');
            if (v) {
                objs = $('form[id="fcimei"]').serialize() + '&taxixkey=' + rd().getkey();
                rd().sendto(u, objs).done(function(rsp) {
                    pmsg = $.parseJSON(rsp);
                    if (pmsg.result == 'success') {
                        rd().messageok('Daftar IMEI', pmsg.message);
                        rd().clsdlg("#dlgiimei");
                        rd().rload(grid);
                    } else {
                        rd().messagebad('Daftar IMEI', pmsg.message);
                    }
                }).fail(function() {
                    rd().messagebad('Daftar IMEI', 'Errors occured.Proccess terminated');
                });
            }
        },
        dlgupt: function() {
            s = $(grid).datagrid('getSelected');
            if(s){
                document.forms['feimei'].reset();
                rd().opndlg("#dlgeimei","Edit IMEI " + s.uniqueid);
                $("#tempuniqueid").val(s.uniqueid);
                $("#feimei").form("load", s);
            }else{
                $.messager.alert('info','Please select/choice data first');
            }
        },
        update: function(u) {
            v = $('#feimei').form('validate');
            if(v){
                objs = $('form[id="feimei"]').serialize() + '&taxixkey=' + rd().getkey();
                rd().sendto(u, objs).done(function(rsp) {
                    pmsg = $.parseJSON(rsp);
                    if (pmsg.result == 'success') {
                        rd().messageok('Edit IMEI', pmsg.message);
                        rd().clsdlg("#dlgeimei");
                        rd().rload(grid);
                    } else {
                        rd().messagebad('Edit IMEI', pmsg.message);
                    }
                }).fail(function() {
                    rd().messagebad('Edit IMEI', 'Errors occured.Proccess terminated');
                });
            }
        },
        del: function(u) {
            s = $(grid).datagrid("getSelected");
            if (s) {
                $.messager.confirm("Remove Imei - Confirmation", "Will remove imei : " + s.uniqueid + " ?", function(c) {
                    if (c) {
                        objs = new Object({
                            'id': s.id,
                            'taxixkey': rd().getkey()
                        });
                        rd().sendto(u, objs).done(function(d) {
                            pmsg = $.parseJSON(d);
                            if (pmsg.result == "success") {
                                rd().messageok("Remove Imei", "DELETE SUCCESS");
                                rd().rload(grid);
                            } else {
                                rd().messagebad('Remove Imei', pmsg.message);
                            }
                        }).fail(function() {
                            rd().messagebad('Remove Imei', 'Errors occured.Proccess terminated');
                        });
                    }
                });
            }
        }
    }
})();
$(function () {
    jImei.init('keys');
    jImei.grid();
    jImei.searchgrid()
});
