"use strict";
var jOrderlogbatal = (function() {
    var hd = "",
        grid = '#gorderlogbatal';

    function init(x) {
        hd = new Gtaxi();
        hd.setkey(x);
    }

    function rd() {
        if (hd instanceof Gtaxi) {
            return hd;
        } else {
            return undefined;
        }
    }
        function a(d, e, g) {
        $(grid).datagrid("load", {
            search : e,
            keyword : g,
            'taxixkey' : rd().getkey()
        });
        setTimeout(function () {
            $('tr>td:contains("' + g + '")').css({
                background : "yellow",
                color : "black",
                "font-weight" : "bold"
            })
        }, 1000)
    }
    function dosearch(g, o, l, d) {
        var n = "",
        b = 1500,
        k = "",
        j = "",
        h = "",
        i = "",
        f = null,
        j = document.getElementById(g),
        h = document.getElementById(o),
        i = d || "1";
        j.onchange = function () {
            h.value = "";
            k = j.options[j.selectedIndex].value;
            if (k == "") {
                return
            }
            h.onkeyup = function () {
                this.value = this.value.toUpperCase().replace(/[^A-Z0-9\s\.\_\-]/g, "");
                f = this.value.toUpperCase().replace(/[^A-Z0-9\s\.\_\-]/g, "");
                if (parseInt(f.length) > parseInt(i)) {
                    clearTimeout(n);
                    n = setTimeout(function () {
                            a(l, k, f)
                        }, b)
                } else {
                    return
                }
            };
            h.keydown = function () {
                clearTimeout(n)
            }
        }
    }
    return {
        init: function(a) {
            init(a);
        },
        searchgrid : function () {
            dosearch("terminalsearch", "terminalkeyword", "gorderlogbatal","0");
        },
        grid: function() {
            $(grid).datagrid({
                url: rd().site_url("orderlogbatal/selects"),
                nowrap: true,
                singleSelect: true,
                rownumbers: true,
                fit: true,
                striped: true,
                autoRowHeight: false,
                pagination: true,
                fitColumns: false,
                method: "post",
                halign: "center",
                queryParams: {
                    'taxixkey': rd().getkey()
                },
                columns: [
                    [{
                        halign: "center",
                        field: "id_log_trx",
                        title: "ID"
                    }, {
                        halign: "center",
                        field: "start_location",
                        title: "Alamat Jemput"
                    }, {
                        halign: "center",
                        field: "end_location",
                        title: "Alamat Tujuan"
                    },{
                        halign: "center",
                        field: "time_start",
                        title: "Request Time"
                    }, {
                        halign: "center",
                        field: "time_end",
                        title: "Response Time"
                    }, {
                        halign: "center",
                        field: "driver_name",
                        title: "Nama Sopir"
                    }
					]
                ]
            });
        }
    }
})();
$(function () {
    jOrderlogbatal.init('keys');
    jOrderlogbatal.grid();
    jOrderlogbatal.searchgrid()
});
