"use strict";
var jTxireq = (function() {
    var hd = "",
        grid = "";
    grid = '#gtaxireq';

    function init(x) {
        hd = new Gtaxi();
        hd.setkey(x);
    }

    function rd() {
        if(hd instanceof Gtaxi){
            return hd;
        }else{
            return undefined;
        }
    }
    return {
        init: function(a) {
            init(a);
        },
        grid: function() {
            $(grid).datagrid({
                url: rd().site_url("taxirequests/selects"),
                nowrap: true,
                singleSelect: true,
                rownumbers: true,
                fit: true,
                striped: true,
                autoRowHeight: false,
                pagination: true,
                fitColumns: false,
                method: "post",
                halign: "center",
                queryParams: {
                    'taxixkey':rd().getkey()
                },
                columns: [
                    [{
                        halign: "center",
                        field: "time_req",
                        title: "TIME REQ"
                    }, {
                        halign: "center",
                        field: "time_rsp",
                        title: "TIME RSP"
                    }, {
                        halign: "center",
                        field: "latitude",
                        title: "LAT",
                        width: 100
                    }, {
                        halign: "center",
                        field: "longitude",
                        title: "LONG",
                        width: 100
                    }]
                ]
            });
        }
    }
})();
